import React from 'react';
import { MapPin, Calendar, User, Star, CheckCircle } from 'lucide-react';

type Listing = {
  id: string;
  type: 'lost' | 'found';
  title: string;
  description: string;
  category: string;
  location: string;
  date_lost_or_found: string;
  is_valuable: boolean;
  created_at: string;
  profiles: {
    username: string;
    trust_score: number;
    successful_reunions: number;
  };
  listing_images: {
    image_url: string;
  }[];
};

type ListingCardProps = {
  listing: Listing;
  onMessage: (listing: Listing) => void;
};

export default function ListingCard({ listing, onMessage }: ListingCardProps) {
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric',
      year: date.getFullYear() !== new Date().getFullYear() ? 'numeric' : undefined
    });
  };

  const primaryImage = listing.listing_images?.[0]?.image_url || 
    'https://images.pexels.com/photos/416978/pexels-photo-416978.jpeg?auto=compress&cs=tinysrgb&w=400';

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden hover:shadow-md transition-shadow">
      {/* Image */}
      <div className="relative">
        <img
          src={primaryImage}
          alt={listing.title}
          className="w-full h-48 object-cover"
        />

        {/* Status Badge */}
        <div className="absolute top-3 left-3">
          <span className={`px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wider ${
            listing.type === 'lost' 
              ? 'bg-red-500 text-white'
              : 'bg-green-500 text-white'
          }`}>
            {listing.type}
          </span>
        </div>

        {/* High Value Badge */}
        {listing.is_valuable && (
          <div className="absolute top-3 right-3">
            <span className="bg-amber-500 text-white px-2 py-1 rounded-full text-xs font-medium">
              High Value
            </span>
          </div>
        )}
      </div>

      {/* Content */}
      <div className="p-4">
        <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2">
          {listing.title}
        </h3>

        <p className="text-gray-600 text-sm mb-3 line-clamp-2">
          {listing.description}
        </p>

        {/* Details */}
        <div className="space-y-2 mb-4">
          <div className="flex items-center text-sm text-gray-500">
            <MapPin className="h-4 w-4 mr-1 flex-shrink-0" />
            <span className="truncate">{listing.location}</span>
          </div>

          <div className="flex items-center text-sm text-gray-500">
            <Calendar className="h-4 w-4 mr-1 flex-shrink-0" />
            <span>{formatDate(listing.date_lost_or_found)}</span>
          </div>
        </div>

        {/* User Info */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <div className="w-6 h-6 bg-gray-300 rounded-full flex items-center justify-center">
              <User className="h-3 w-3 text-gray-600" />
            </div>
            <div className="flex items-center space-x-1">
              <span className="text-sm font-medium text-gray-700">
                {listing.profiles?.username}
              </span>
              {listing.profiles?.successful_reunions > 0 && (
                <div className="flex items-center">
                  <CheckCircle className="h-3 w-3 text-green-500 fill-current" />
                  <span className="text-xs text-green-600 ml-1">
                    {listing.profiles.successful_reunions}
                  </span>
                </div>
              )}
            </div>
          </div>

          <button
            onClick={() => onMessage(listing)}
            className="px-3 py-1.5 bg-blue-600 text-white text-sm font-medium rounded-lg hover:bg-blue-700 transition-colors"
          >
            Message
          </button>
        </div>
      </div>
    </div>
  );
}

files.src/components/Messages/MessageModal.tsx:
import React, { useState } from 'react';
import { X, Send, AlertTriangle } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import { supabase } from '../../lib/supabase';

type Listing = {
  id: string;
  title: string;
  profiles: {
    id: string;
    username: string;
  };
};

type MessageModalProps = {
  isOpen: boolean;
  onClose: () => void;
  listing: Listing | null;
};

export default function MessageModal({ isOpen, onClose, listing }: MessageModalProps) {
  const { user } = useAuth();
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [sent, setSent] = useState(false);

  if (!isOpen || !listing) return null;

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!user || !message.trim()) return;

    setLoading(true);

    try {
      const { error } = await supabase
        .from('messages')
        .insert({
          sender_id: user.id,
          receiver_id: listing.profiles.id,
          listing_id: listing.id,
          content: message.trim(),
        });

      if (error) throw error;

      setSent(true);
      setTimeout(() => {
        onClose();
        setMessage('');
        setSent(false);
      }, 2000);
    } catch (error) {
      console.error('Error sending message:', error);
    } finally {
      setLoading(false);
    }
  };

  if (sent) {
    return (
      <div className="fixed inset-0 z-50 overflow-y-auto">
        <div className="flex min-h-full items-center justify-center p-4">
          <div className="fixed inset-0 bg-black bg-opacity-25" onClick={onClose} />

          <div className="relative bg-white rounded-lg shadow-xl max-w-md w-full p-6 text-center">
            <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Send className="h-8 w-8 text-green-600" />
            </div>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              Message Sent!
            </h3>
            <p className="text-gray-600">
              Your message has been sent to {listing.profiles.username}. 
              They'll be able to respond through their messages.
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex min-h-full items-center justify-center p-4">
        <div className="fixed inset-0 bg-black bg-opacity-25" onClick={onClose} />

        <div className="relative bg-white rounded-lg shadow-xl max-w-md w-full p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-900">
              Contact {listing.profiles.username}
            </h2>
            <button
              onClick={onClose}
              className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <X className="h-5 w-5 text-gray-500" />
            </button>
          </div>

          <div className="mb-4 p-3 bg-gray-50 rounded-lg">
            <p className="text-sm text-gray-600 mb-1">Regarding:</p>
            <p className="font-medium text-gray-900">{listing.title}</p>
          </div>

          {/* Safety Warning */}
          <div className="mb-4 p-3 bg-amber-50 border border-amber-200 rounded-lg">
            <div className="flex items-start">
              <AlertTriangle className="h-5 w-5 text-amber-500 flex-shrink-0 mt-0.5" />
              <div className="ml-2">
                <p className="text-sm font-medium text-amber-800">Safety Tips</p>
                <ul className="text-xs text-amber-700 mt-1 space-y-1">
                  <li> Meet in public places</li>
                  <li> Never share personal information</li>
                  <li> Don't send money or payments</li>
                </ul>
              </div>
            </div>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="message" className="block text-sm font-medium text-gray-700 mb-2">
                Your Message
              </label>
              <textarea
                id="message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder={`Hi ${listing.profiles.username}, I saw your listing about "${listing.title}". I'd like to get in touch about this item.`}
                rows={4}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
              />
              <p className="text-xs text-gray-500 mt-1">
                Be specific about the item to help verify ownership
              </p>
            </div>

            <div className="flex justify-end space-x-3">
              <button
                type="button"
                onClick={onClose}
                className="px-4 py-2 border border-gray-300 text-gray-700 text-sm font-medium rounded-lg hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={loading || !message.trim()}
                className="px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-lg hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors inline-flex items-center"
              >
                {loading ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2" />
                    Sending...
                  </>
                ) : (
                  <>
                    <Send className="h-4 w-4 mr-2" />
                    Send Message
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

